export default class HomeScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      avatarCurrency: 0,
      items: {},
    };
    if (!fb.apps.length) {
      fb.initializeApp(ApiKeys.FirebaseConfig);
    }
    this.readData();
  }

  readData() {
    fb.database()
      .ref("response/" + userid + "/currency")
      .once("value", (dataSnapShot) => {
        baseCurrency = dataSnapShot.val();
      });
  }

  componentDidMount() {
    fb.database()
      .ref("response/" + userid + "/shop")
      .on("value", (querySnapShot) => {
        let data = querySnapShot.val() ? querySnapShot.val() : {};
        let shopitems = { ...data };
        this.setState({
          items: shopitems,
        });
      });
    fb.database()
      .ref("response/" + userid + "/currency")
      .on("value", (querySnapShot) => {
        let data = querySnapShot.val() ? querySnapShot.val() : {};
        this.setState({
          avatarCurrency: data,
        });
      });
  }

  render() {
    return (
      <View style={styles.container}>
        {/* <topNaviBar /> */}
        <ImageBackground
          source={require("../assets/BackgroundOrange.png")}
          style={styles.backgroundImage}
        >
          <View style={styles.avatarContainer}>
            <Text>Your currency: {this.state.avatarCurrency}</Text>
          </View>
          <AppIndex />
        </ImageBackground>
      </View>
    );
  }
}

