import React, { Component } from "react";
import { StyleSheet, ImageBackground, Text, Image, View, TouchableOpacity, StatusBar } from "react-native";
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';
import { ProgressBar, Colors } from "react-native-paper";
import { RFValue } from "react-native-responsive-fontsize";
import { images, getBackgroundImage } from "../components/images";
import Header from "../components/Header";

export default class HomeScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      avatarCharacter: 0,
      avatarStatus: 100, //should be changed based on data in the database 
      avatarHealth: 100, //should be changed based on data in the database 
      backgroundColor: 5,
      avatarCurrency: 0,
    };

    () => this.readData();
  }

  handleAvatarHealthChange = (props) => {
    const avatarHealth = props.health;
    const avatarCharacter = this.state.avatarCharacter;
    if (avatarCharacter === 1) {
      return <Image style={styles.avatar} source={images.character1} />;
    } else if (avatarCharacter === 2) {
      return <Image style={styles.avatar} source={images.character2} />;
    } else if (avatarCharacter === 3) {
      return <Image style={styles.avatar} source={images.character3} />;
    } else {
      if (avatarHealth > 80) {
        return (
          <Image
            style={styles.avatar}
            source={require("../assets/avatar/avatar_1.png")}
          />
        );
      } else if (avatarHealth > 60 && avatarHealth <= 80) {
        return (
          <Image
            style={styles.avatar}
            source={require("../assets/avatar/avatar_2.png")}
          />
        );
      } else if (avatarHealth > 40 && avatarHealth <= 60) {
        return (
          <Image
            style={styles.avatar}
            source={require("../assets/avatar/avatar_3.png")}
          />
        );
      } else {
        return (
          <Image
            style={styles.avatar}
            source={require("../assets/avatar/avatar_4.png")}
          />
        );
      }
    }
  };

  render() {
    const background = getBackgroundImage(this.state.backgroundColor);
    return (
      <View style={styles.container}>

        <ImageBackground source={background} style={styles.backgroundImage}>
          <Header route={this.props} />
          <View style={styles.avatarContainer}>
            <Text style={styles.avatarDialogue}> Another Day to be with you. </Text>
            <this.handleAvatarHealthChange
              health={this.state.avatarHealth}
            />
            <View>
              <Text style={styles.textStyle}> Happiness Status </Text>
              <ProgressBar
                progress={1 * this.state.avatarStatus * 0.01}
                color={Colors.lightBlue300}
                style={{
                  width: 200,
                  height: 13,
                }}
              />
            </View>
            <Text> </Text>
            <View>
              <Text style={styles.textStyle}> Emotion Status </Text>
              <ProgressBar
                progress={1 * this.state.avatarHealth * 0.01}
                color={Colors.white}
                style={{
                  width: 200,
                  height: 13,
                }}
              />
            </View>
          </View>

          <View style={styles.buttonContainer}>
            <TouchableOpacity
              style={styles.customBtnBG}
              onPress={() => this.props.navigation.navigate("Quest")}
            >
              <Text style={styles.customBtnText}>Quest</Text>
            </TouchableOpacity>

            <TouchableOpacity
              style={styles.customBtnBG}
              onPress={() =>
                this.props.navigation.navigate("Nutritional")
              }
            >
              <Text style={styles.customBtnText}>Nutrition</Text>
            </TouchableOpacity>

            <TouchableOpacity
              style={styles.customBtnBG}
              onPress={() => this.props.navigation.navigate("Shop")}
            >
              <Text style={styles.customBtnText}>Shop</Text>
            </TouchableOpacity>
          </View>
        </ImageBackground>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#f0f8ff",
    flexDirection: "column",
  },

  backgroundImage: {
    height: hp('100%'),
    width: wp('100%'),
    justifyContent: "center",
  },

  avatarContainer: {
    flex: 2,
    alignItems: "center",
    justifyContent: "center",
  },

  avatarDialogue: {
    alignItems: "center",
    color: "#000000",
    borderWidth: 0.5,
    borderRadius: 10,
    width: "65%",
    marginTop: "1%",
    justifyContent: "center",
    fontSize: RFValue(25),
    fontWeight: "400",
  },

  avatar: {
    height: wp('50%'),
    width: wp('50%'),
    margin: 35,
  },

  buttonContainer: {
    flex: 1,
    alignItems: "center",
    alignContent: "center",
  },

  customBtnText: {
    fontSize: 24,
    fontWeight: "300",
    color: "#990000",
    textAlign: "center",
  },

  customBtnBG: {
    backgroundColor: "#FFE5CC",
    borderRadius: 5,
    marginLeft: 15,
    marginBottom: 10,
    marginTop: 5,
    width: 150,
    height: 30,
    flexDirection: "column",
  },

  textStyle: {
    fontSize: 14,
    fontWeight: "500",
    color: "#990000",
    textAlign: "left",
  },
});
